// === Helpers ===
const num = (v) => (isFinite(+v) ? +v : 0);
const toISO = (d) => d.toISOString().slice(0,10);
const parse = (s) => { const d = new Date(s); return isNaN(d) ? null : d; };
const inWindowMaker = (fromISO, toISO_) => (s) => {
  const d = parse(s);
  return d && d >= parse(fromISO) && d <= parse(toISO_);
};

// Safe fetch of items from a node by any of candidate names
function safeItems(candidates) {
  for (const name of candidates) {
    try {
      const arr = $items(name, 0, 0);
      if (Array.isArray(arr)) return arr.map(i => i.json);
    } catch (e) { /* ignore */ }
  }
  return [];
}

// === Resolve data range (3 months) without needing Set Params ===
const now = new Date();
const from = new Date(Date.UTC(now.getUTCFullYear(), now.getUTCMonth()-3, now.getUTCDate()));
const DATE_TO = toISO(now);
const DATE_FROM = toISO(from);
const inWindow = inWindowMaker(DATE_FROM, DATE_TO);

// === Read the 4 sources (tolerant names) ===
const PEOPLE = safeItems(['Get People','People','GET People','GetPeople']);
const ACCOUNTS = safeItems(['Get Accounts','Accounts','GET Accounts','GetAccounts']);
const TXS = safeItems(['Get Transactions','Transactions','GET Transactions','GetTransactions']);
const DASH = safeItems(['Get Dashboard','Dashboard','GET Dashboard','GetDashboard']);

// If nothing came, fail early with a clear message
if (!PEOPLE.length) {
  return [{ json: { error: true, message: 'People vazio: confira o nome do nÃ³ (ex.: "Get People") e se ele executa antes deste Code node.' } }];
}

// --- Indexers ---
const accountsByLink = new Map();   // link_id -> [account]
const accountsByKey  = new Map();   // (link|account_number) -> account

for (const a of ACCOUNTS) {
  const link = a.link_id || a.link || '';
  if (!link) continue;
  const accNumber = (a.account_number ?? a.number ?? '').toString();
  const key = accNumber ? `${link}|${accNumber}` : null;

  const accountObj = {
    account_id: a.account_id || a.id || '',
    account_name: a.account_name || a.name || '',
    account_number: accNumber || '',
    institution_name: a.institution_name || '',
    institution_type: a.institution_type || '',
    currency: a.currency || '',
    balance_current: num(a.balance_current),
    balance_available: num(a.balance_available),
    funds_total: num(a.funds_total),
    liability_outstanding: num(a.liability_outstanding),
    balance_type: a.balance_type || '',
    last_updated: a.last_updated || a.collected_at || a.created_at || ''
  };

  if (!accountsByLink.has(link)) accountsByLink.set(link, []);
  accountsByLink.get(link).push(accountObj);
  if (key) accountsByKey.set(key, accountObj);
}

// Transactions index: prefer account_id; fallback link+account_number
const txByAccount = new Map(); // account_id -> [tx]
const txByKey = new Map();     // (link|account_number) -> [tx]

for (const t of TXS) {
  const link = t.link_id || t.link || '';
  const accId = t.account_id || (t.account && (t.account.id || t.account.account_id)) || '';
  const accNumber = (t.account_number ?? t.number ?? '').toString();
  const key = (link && accNumber) ? `${link}|${accNumber}` : null;

  const date = t.value_date || t.posted_date || t.date || t.booking_date || '';
  if (!inWindow(date)) continue;

  const txObj = {
    transaction_id: t.transaction_id || t.id || '',
    amount: num(t.amount),
    currency: t.currency || '',
    category: t.category || '',
    description: t.description || t.concept || '',
    value_date: date,
    type: t.type || '',
    status: t.status || ''
  };

  if (accId) {
    if (!txByAccount.has(accId)) txByAccount.set(accId, []);
    txByAccount.get(accId).push(txObj);
  } else if (key) {
    if (!txByKey.has(key)) txByKey.set(key, []);
    txByKey.get(key).push(txObj);
  }
}

// Dashboard 1:1
const dashByLink = new Map();
for (const d of DASH) {
  const link = d.link_id || d.link || '';
  if (!link) continue;
  dashByLink.set(link, d);
}

// --- Build nested owners (max = PEOPLE length, ex.: 42) ---
const out = [];

for (const p of PEOPLE) {
  const link = p.link_id || p.link || '';
  if (!link) continue;

  const ownerBase = {
    link_id: link,
    owner_id: p.owner_id || '',
    person_alias: p.person_alias || p.display_name || '',
    first_name: p.first_name || '',
    last_name: p.last_name || '',
    email: p.email || '',
    phone: p.phone || '',
    document_type: p.document_type || '',
    document_number: p.document_number || '',
    created_at: p.created_at || '',
    collected_at: p.collected_at || '',
    src: p.src || 'people',
    window_from: DATE_FROM,
    window_to: DATE_TO
  };

  const dashboard = dashByLink.get(link);
  if (dashboard) ownerBase.dashboard = dashboard;

  const ownerAccounts = (accountsByLink.get(link) || []).map(acc => {
    let txList = (acc.account_id && txByAccount.get(acc.account_id)) || [];
    if ((!txList || txList.length === 0) && acc.account_number) {
      const k = `${link}|${acc.account_number}`;
      txList = txByKey.get(k) || [];
    }
    txList.sort((a,b) => (b.value_date || '').localeCompare(a.value_date || ''));
    return { ...acc, transactions: txList };
  });

  out.push({ json: { ...ownerBase, accounts: ownerAccounts } });
}

return out;

const now = new Date();
const yyyy = now.getUTCFullYear();
const mm = String(now.getUTCMonth()+1).padStart(2,'0');
const dd = String(now.getUTCDate()).padStart(2,'0');
const date_to = `${yyyy}-${mm}-${dd}`;
const from = new Date(Date.UTC(now.getUTCFullYear(), now.getUTCMonth()-3, now.getUTCDate()));
const fyyyy = from.getUTCFullYear();
const fmm = String(from.getUTCMonth()+1).padStart(2,'0');
const fdd = String(from.getUTCDate()).padStart(2,'0');
const date_from = `${fyyyy}-${fmm}-${fdd}`;
return [{ json: { date_from, date_to, model: 'llama3:8b', temperature: 0.2, currency: 'BRL' } }];

// Prep Insights v4 (curto e direto)
const o = $json;
const ACCS = Array.isArray(o.accounts) ? o.accounts : [];
const num = v => (isFinite(+v) ? +v : 0);

let fromISO = o.window_from, toISO_ = o.window_to;
if (!fromISO || !toISO_) {
  const now = new Date();
  const from = new Date(Date.UTC(now.getUTCFullYear(), now.getUTCMonth()-3, now.getUTCDate()));
  fromISO = from.toISOString().slice(0,10);
  toISO_  = now.toISOString().slice(0,10);
}

let assets=0, liab=0, bal=0, txc=0, inflow=0, outflow=0;
const topAcc = [];
const cat = new Map();

for (const a of ACCS) {
  const b = num(a.balance_current ?? a.funds_total ?? 0);
  bal += b;
  if ((a.balance_type||'').toUpperCase()==='LIABILITY') liab += Math.abs(b); else assets += Math.max(b,0);
  topAcc.push({account_id:a.account_id||'',account_name:a.account_name||a.name||'',institution_name:a.institution_name||'',balance_current:b,currency:a.currency||'BRL'});
  for (const t of (a.transactions||[])) {
    const amt = num(t.amount);
    if (amt>=0) inflow+=amt; else outflow+=Math.abs(amt);
    const c = String(t.category||'uncategorized').toLowerCase();
    cat.set(c, (cat.get(c)||0) + Math.abs(amt));
  }
}
topAcc.sort((x,y)=>(y.balance_current||0)-(x.balance_current||0));
const topCats = [...cat.entries()].sort((a,b)=>b[1]-a[1]).slice(0,4).map(([category,total])=>({category,total:+total.toFixed(2)}));

const metrics = {
  link_id: o.link_id||'',
  owner_id: o.owner_id||'',
  person: { alias:o.person_alias||o.display_name||'', first_name:o.first_name||'', last_name:o.last_name||'', email:o.email||'' },
  window: { from: fromISO, to: toISO_ },
  accounts_summary: {
    count: ACCS.length, assets_total:+assets.toFixed(2), liabilities_total:+liab.toFixed(2),
    balance_total:+bal.toFixed(2), top_accounts: topAcc.slice(0,3),
  },
  transactions_summary: {
    count: txc, inflow_sum:+inflow.toFixed(2), outflow_sum:+outflow.toFixed(2),
    net_flow:+(inflow-outflow).toFixed(2), top_categories: topCats
  },
  currency: 'BRL',
};

// prompt curto + âno-genericâ
const system = 'Responda SOMENTE JSON vÃ¡lido UTF-8 no formato {"insights":[...]}.';
const user = `Contexto BRL (compacto):
${JSON.stringify({
  link_id: metrics.link_id,
  window: metrics.window,
  accounts_summary: metrics.accounts_summary,
  transactions_summary: metrics.transactions_summary
})}
Regras:
- AtÃ© 3 insights Ãºteis, nada genÃ©rico.
- Use dados: concentraÃ§Ã£o por conta, categorias, inflow/outflow, qualidade de dados (ex.: uncategorized alto, outflow=0).
- Campos curtos (<=140 chars). category â ["spending","income","savings","subscriptions","alerts","investments","cashflow"].

Schema:
{"type":"object","required":["insights"],"properties":{"insights":{"type":"array","maxItems":3,"items":{"type":"object","required":["title","summary","category","priority","confidence","suggested_action"],"properties":{"title":{"type":"string"},"summary":{"type":"string"},"category":{"type":"string"},"priority":{"type":"string","enum":["low","medium","high"]},"confidence":{"type":"number","minimum":0,"maximum":1},"suggested_action":{"type":"string"}}}}}}
SaÃ­da: SOMENTE {"insights":[...]}.`;

return [{
  json: {
    link_id: metrics.link_id,
    owner_id: metrics.owner_id,
    date_from: metrics.window.from,
    date_to: metrics.window.to,
    metrics_json: JSON.stringify(metrics),
    system,              // <â adiciona
    user,                // <â adiciona
    body: {
      model: "llama3.2:3b",
      messages: [
        { role: "system", content: system },
        { role: "user",   content: user }
      ],
      stream: false,
      format: "json",
      keep_alive: "10m",
      options: {
        temperature: 0.1,
        top_p: 0.9,
        num_predict: 450,   // â evita corte
        num_ctx: 1024,
        num_batch: 128,
        num_gpu: 999
      }
    }
  }
}];

// Parse resposta do Ollama (HTTP Request) e solta 1 item por insight.
// Robusto contra JSON com ruÃ­do e cortes.

const prev = $item(0).$node["Prep Insights"].json; // contexto do item atual

// Em algumas execuÃ§Ãµes o HTTP node pode vir como array; padroniza:
const res = $json;

// Tenta extrair o conteÃºdo textual do modelo:
let content = (res && res.message && typeof res.message.content === 'string')
  ? res.message.content
  : '';

// FunÃ§Ã£o de parse tolerante
function safeParse(s) {
  if (!s || typeof s !== 'string') return null;
  // 1) tenta direto
  try { return JSON.parse(s); } catch {}
  // 2) remove crases/backticks e tenta de novo
  const noTicks = s.replace(/^```json|^```|```$/g, '');
  try { return JSON.parse(noTicks); } catch {}
  // 3) pega de { atÃ© } (Ãºltimo) e tenta
  const first = s.indexOf('{');
  const last  = s.lastIndexOf('}');
  if (first !== -1 && last !== -1 && last > first) {
    const mid = s.slice(first, last + 1);
    try { return JSON.parse(mid); } catch {}
  }
  return null;
}

let parsed = safeParse(content);
if (!parsed || typeof parsed !== 'object') parsed = { insights: [] };

const insights = Array.isArray(parsed.insights) ? parsed.insights : [];

const meta = {
  model: res.model,
  created_at: res.created_at,
  prompt_tokens: res.prompt_eval_count ?? null,
  completion_tokens: res.eval_count ?? null,
};

function rowFrom(ins) {
  return {
    insight_id: `${prev.link_id}-${(Math.random().toString(36).slice(2,10))}`,
    link_id: prev.link_id,
    source_from: prev.date_from,
    source_to: prev.date_to,
    generated_at: new Date().toISOString(),

    title: ins.title,
    insight: ins.summary,
    category: ins.category,
    priority: ins.priority,
    action: ins.suggested_action,
    confidence: ins.confidence,

    metrics_json: prev.metrics_json,
    model: meta.model,
    temperature: prev.temperature ?? 0.1,
    prompt_tokens: meta.prompt_tokens,
    completion_tokens: meta.completion_tokens,
  };
}

if (insights.length === 0) {
  return [{
    json: rowFrom({
      title: "Sem JSON vÃ¡lido do modelo",
      summary: "Modelo nÃ£o retornou {insights} ou veio truncado.",
      category: "alerts",
      priority: "low",
      suggested_action: "Reexecutar com prompt rÃ­gido ou aumentar num_predict.",
      confidence: 0,
    }),
  }];
}

return insights.map((ins) => ({ json: rowFrom(ins) }));
